if (flow.get("orderId") == msg.payload.orderId || flow.get("orderId") == "0") {
    // orderId im Flow speichern
    flow.set("orderId", msg.payload.orderId);

    // orderUpdateId Fallunterscheidung
    if (msg.payload.orderUpdateId > flow.get("orderUpdateId")) {

        // duration in actionState
        let actionState = flow.get("actionState");
        actionState.command = msg.payload.action.command;
        actionState.id = msg.payload.action.id;
        let loads = flow.get("loads");
        let newLoad = loads[0] ?? {};

        // Command Fallunterscheidung
        if (actionState.command == "PICK" && flow.get("moduleState") == "IDLE") {
            // ModulState busy setzen
            flow.set("moduleState", "PICKBUSY");

            // OPCUA Write Content
            msg.payload.valuesToWrite = [{ "value": true }];
            msg.payload.nodesToWrite = [{ "name": "pick", "nodeId": "ns=4;i=5", "datatypeName": "Boolean" }];
        } else if (actionState.command == "DROP" && flow.get("moduleState") == "WAITING_AFTER_MILL") {
            // ModulState busy setzen
            flow.set("moduleState", "DROPBUSY");

            // OPCUA Write Content
            msg.payload.valuesToWrite = [{ "value": true }];
            msg.payload.nodesToWrite = [{ "nodeId": "ns=4;i=6", "datatypeName": "Boolean" }];
        } else if (actionState.command == "MILL" && flow.get("moduleState") == "WAITING_AFTER_PICK") {
            // state.loads bearbeiten
            newLoad.duration = msg.payload.action.metadata.duration ? msg.payload.action.metadata.duration : 2

            // ModulState busy setzen
            flow.set("moduleState", "MILLBUSY");

            // OPCUA Write Content
            msg.payload.valuesToWrite = [
                { "value": true },
                { "value": newLoad.duration}];
            msg.payload.nodesToWrite = [
                { "name": "mill", "nodeId": "ns=4;i=4", "datatypeName": "Boolean" },
                { "name": "duration", "nodeId": "ns=4;i=11", "datatypeName": "Int16" }];
            actionState.duration = newLoad.duration;
        } else {
            const flowErrors = flow.get("errors") ?? [];
            const errors = Array.isArray(flowErrors) ? flowErrors : [flowErrors];
            let error = {
                "errorType": "Validation",
                "errorLevel": "WARNING",
                "errorReferences": [
                    { "topic": "order" },
                    { "headerId": flow.get("headerId") },
                    { "orderId": msg.payload.orderId },
                    { "orderUpdateId": msg.payload.orderUpdateId }
                ],
                "errorMessage": "Command not supported"
            }
            // Error in Flow
            flow.set("errors", [...errors, error]);
            return [null, msg];
        }

        // actionState in Flow speichern
        flow.set("actionState", actionState);

        // loads for order im Flow speichern
        newLoad.loadType = msg.payload.action.metadata.type;
        flow.set("loads", [newLoad]);

        // orderUpdateId updaten
        flow.set("orderUpdateId", msg.payload.orderUpdateId);

        // OPCUA write Content
        msg.payload.nodetype = "inject";
        msg.payload.injectType = "write";

        return [msg,null];
    } else {
        const flowErrors = flow.get("errors") ?? [];
        const errors = Array.isArray(flowErrors) ? flowErrors : [flowErrors];
        let error = {
            "errorType": "Validation",
            "errorLevel": "WARNING",
            "errorReferences": [
                { "topic": "order" },
                { "headerId": flow.get("headerId") },
                { "orderId": msg.payload.orderId },
                { "orderUpdateId": msg.payload.orderUpdateId }
            ],
            "errorMessage": "OrderUpdateId not valid"
        }
        // Error in Flow
        flow.set("errors", [...errors, error]);
        return [null, msg];
    }
} else {
    const flowErrors = flow.get("errors") ?? [];
    const errors = Array.isArray(flowErrors) ? flowErrors : [flowErrors];
    let error = {
        "errorType": "Validation",
        "errorLevel": "WARNING",
        "errorReferences": [
            { "topic": "order" },
            { "headerId": flow.get("headerId") },
            { "orderId": msg.payload.orderId },
            { "orderUpdateId": msg.payload.orderUpdateId }
        ],
        "errorMessage": "OrderId not valid"
    }
    // Error in Flow
    flow.set("errors", [...errors, error]);
    return [null, msg];
}

// wenn "TRUE" im OPCUA und die richtige interne state
if (msg.payload.value.value.value == true && flow.get("moduleState") == "PICKBUSY") {

    // state
    let state = flow.get("state");
    let headerId = flow.get("headerId");
    let actionState = flow.get("actionState");
    let flowErrors = flow.get("errors") ?? [];
    let errors = Array.isArray(flowErrors) ? flowErrors : [flowErrors];

    // Timestamp
    let now = new Date();
    let timestamp = now.toISOString();

    // erfolgreich mit der NodeId
    if (msg.payload.nodeId == "ns=4;i=8") {
        if (msg.payload.value.value.value == true && flow.get("moduleState") == "PICKBUSY") {
            flow.set("moduleState", "WAITING_AFTER_PICK");
            actionState.state = "FINISHED"; // Waiting for FTS
        }
    }

    // nicht erfolgreich mit der NodeId
    else if (msg.payload.nodeId == "ns=4;i=12") {
        let error = {
            timestamp,
            "errorType": "PICK_failed",
            "errorMessage": "PICK failed",
            "errorLevel": "FATAL",
            "errorReferences": [
                { "topic": "order" },
                { "headerId": flow.get("headerId") },
                { "orderId": flow.get("orderId") },
                { "orderUpdateId": flow.get("orderUpdateId") }
            ],
        }
        actionState.state = "FAILED"; // FAILED PICK
        errors.push(error);
        flow.set("errors", errors);
        flow.set("moduleState", "IDLE");
    }

    state.headerId = headerId;
    state.loads = flow.get("loads");
    state.actionState = flow.get("actionState");
    state.timestamp = timestamp;
    state.orderId = flow.get("orderId");
    state.orderUpdateId = flow.get("orderUpdateId");
    state.errors = flow.get("errors") ? flow.get("errors") : [];

    // headerId Update
    flow.set("headerId", headerId + 1);

    msg.topic = flow.get("MQTT_topic") + "/state";
    msg.payload = state;
    return msg;
} else {

    return;
}

