/**
 * @typedef {object} ActionState
 * @property {string} id
 * @property {string} state
 * @property {string} command
 * @property {object} [metadata]
 * 
 * @param {string} state
 * @returns {ActionState}
 */
function updateActionState(state, metadata = undefined) {
    const actionState = flow.get("actionState") ?? {};
    actionState.id = msg.actionId;
    actionState.state = state;
    actionState.command = msg.actionCommand;
    actionState.metadata = metadata ?? undefined;
    flow.set("actionState", actionState);
    return actionState;
}

